        this.element.attr("stroke", "#f00");
        this.element.attr("fill", "#fff");
    }

    /**
     * Event for hover-in action.
     */
    onHoverIn() {
        this.element.attr("r", 7);
        this.element.attr("stroke", "#f00");
    }

    /**
     * Event for hover-out action.
     */
    onHoverOut() {
        this.element.attr("r", 5);
        this.element.attr("stroke", DEFAULT_STROKE_COLOR);
    }

